import React, { useState } from "react";
import clsx from "clsx";
import { fade, makeStyles, useTheme } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
// import InfoIcon
import CssBaseline from "@material-ui/core/CssBaseline";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import List from "@material-ui/core/List";
import Typography from "@material-ui/core/Typography";
import Divider from "@material-ui/core/Divider";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";
import ChevronLeftIcon from "@material-ui/icons/ChevronLeft";
import ChevronRightIcon from "@material-ui/icons/ChevronRight";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import AccountCircleRoundedIcon from "@material-ui/icons/AccountCircleRounded";
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import HistoryIcon from "@material-ui/icons/History";
import GavelIcon from "@material-ui/icons/Gavel";
import VerifiedUserIcon from "@material-ui/icons/VerifiedUser";
import HomeIcon from "@material-ui/icons/Home";
import VpnKeyIcon from "@material-ui/icons/VpnKey";
import MeetingRoomIcon from "@material-ui/icons/MeetingRoom";
import SearchIcon from "@material-ui/icons/Search";
import ChatIcon from "@material-ui/icons/Chat";
import Button from "@material-ui/core/Button";

import Axios from "axios";
import { Link, useHistory } from "react-router-dom";

const jwt = require("jsonwebtoken");
const env = require("dotenv");
env.config();

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  appBar: {
    transition: theme.transitions.create(["margin", "width"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },

  menuButton: {
    marginRight: theme.spacing(2),
  },
  hide: {
    display: "none",
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
  },
  drawerHeader: {
    display: "flex",
    alignItems: "center",
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: "flex-end",
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: -drawerWidth,
  },
  contentShift: {
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: 0,
  },
  title: {
    flexGrow: 1,
  },
}));

export default function Header() {
  const classes = useStyles();
  const theme = useTheme();
  const history = useHistory();

  const [open, setOpen] = React.useState(false);
  const handleDrawerOpen = () => {
    setOpen(true);
  };
  const [lococart, setLococart] = React.useState(true);
  const showLococart = () => {
    if (window.innerWidth < 960) {
      setLococart(false);
    } else {
      setLococart(true);
    }
  };
  const [userId, setUserId] = useState("");
  const [UserName, setUserName] = useState("");
  const [isCustomer, setIsCustomer] = useState(true);
  React.useEffect(async () => {
    showLococart();
    try {
      var jwtToken = await localStorage.getItem("userinfo");
      console.log(jwt);
      if (jwtToken === null) {
        jwtToken = await localStorage.getItem("userinfo");
        const user = await jwt.verify(
          jwtToken,
          "1234567uyasdfgghhjjkxvcbvbnmn567fhgjh234567556677"
        );
        if (user) {
          setUserId(user._id);
          console.log(user);
          const resp = await Axios.get(
            `http://localhost:3001/api/student/${user._id}`
          )
            .then(function (response) {
              console.log(response);
              const customerName = response.data.student.firstName;
              setUserName(response.data.student.firstName);
              console.log("User: " + customerName);
              setIsCustomer(false);
            })
            .catch(function (error) {
              console.log(error);
            });
        }
      } else {
        const user = await jwt.verify(
          jwtToken,
          "1234567uyasdfgghhjjkxvcbvbnmn567fhgjh234567556677"
        );
        if (user) {
          setUserId(user._id);
          console.log(user);
          const resp = await Axios.get(
            `http://localhost:3001/api/student/${user._id}`
          )
            .then(function (response) {
              console.log(response);
              const customerName = response.data.student.firstName;
              setUserName(response.data.student.firstName);
              console.log("CustomerName: " + customerName);
            })
            .catch(function (error) {
              console.log(error);
            });
        }
      }
    } catch (e) {
      console.log(e);
    }
  }, []);
  window.addEventListener("resize", showLococart);

  
 

  const handleDrawerClose = () => {
    setOpen(false);
  };

  const logoutHandler = (e) => {
    e.preventDefault();
    localStorage.removeItem("userinfo");
    history.push("/");
    window.location.reload(false);
  };
  
  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar
        position="fixed"
        className={clsx(classes.appBar, {
          [classes.appBarShift]: open,
        })}
      >
        <Toolbar>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            onClick={handleDrawerOpen}
            edge="start"
            className={clsx(classes.menuButton, open && classes.hide)}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" className={classes.title}>
            <ListItem>
              {lococart && (
                <ListItemIcon>
                  <b style={{ color: "#ffffff" }}>LOCOCART</b>
                </ListItemIcon>
              )}
             
            </ListItem>
          </Typography>
          {userId === "" ? (
            <Button
              color="inherit"
              style={{ marginLeft: "auto" }}
              href="/signin"
            >
              Login
            </Button>
          ) : (
            <div>
             
              <Button
                color="inherit"
                style={{ marginLeft: "auto" }}
                onClick={logoutHandler}
              >
                Logout
              </Button>
            </div>
          )}
        </Toolbar>
      </AppBar>
      <Drawer
        className={classes.drawer}
        variant="persistent"
        anchor="left"
        open={open}
        classes={{
          paper: classes.drawerPaper,
        }}
      >
        <div className={classes.drawerHeader}>
          <IconButton onClick={handleDrawerClose}>
            {theme.direction === "ltr" ? (
              <div style={{ fontFamily: "Trebuchet MS" }}>
                <i>
                  Hi, <b>{UserName + " "}</b>
                </i>
                <ChevronLeftIcon />
              </div>
            ) : (
              <div>
                {UserName}
                <ChevronRightIcon />
              </div>
            )}
          </IconButton>
        </div>

        {userId === "" ? (
          <List></List>
        ) : isCustomer ? (
          <List>
            <Divider />
            
          </List>
        ) : (
          <List>
            <Divider />
          </List>
        )}

        <Divider />
        <List>
          <Link to="/" onClick={handleDrawerClose}>
            <ListItem button key="Home Screen">
              <ListItemIcon>
                <HomeIcon />
              </ListItemIcon>
              <ListItemText
                primary="Home Screen"
                style={{ color: "#000000" }}
              />
            </ListItem>
          </Link>
         
          <Link to="/signin" onClick={handleDrawerClose}>
            <ListItem button key="Register/SignIn">
              <ListItemIcon>
                <VpnKeyIcon />
              </ListItemIcon>
              <ListItemText
                primary="Register/SignIn"
                style={{ color: "#000000" }}
              />
            </ListItem>
          </Link>
         
          <Link to="/feedback" onClick={handleDrawerClose}>
            <ListItem button key="Feedback">
              <ListItemIcon>
                <ChatIcon />
              </ListItemIcon>
              <ListItemText primary="Feedback" style={{ color: "#000000" }} />
            </ListItem>
          </Link>
          <Link to="/aboutus" onClick={handleDrawerClose}>
            <ListItem button key="About Us">
              <ListItemIcon>
                {/* <InfoIcon /> */}
              </ListItemIcon>
              <ListItemText primary="About Us" style={{ color: "#000000" }} />
            </ListItem>
          </Link>
          <Link to="/logout" onClick={handleDrawerClose}>
            <ListItem button key="Logout">
              <ListItemIcon>
                <MeetingRoomIcon />
              </ListItemIcon>
              <ListItemText primary="Logout" style={{ color: "#000000" }} />
            </ListItem>
          </Link>
        </List>
      </Drawer>
    </div>
  );
}
