import React from "react";
import { Media } from "react-bootstrap";
// import "../Category/SubCategory/subcategory.scss";
import Card from '@material-ui/core/Card';
import Link from '@material-ui/core/Link';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import MIT from '../../images/LocoCart.PNG';
import clsx from "clsx";
import { makeStyles } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
// import Button from "@material-ui/core/Button";
import List from "@material-ui/core/List";
import Divider from "@material-ui/core/Divider";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import InboxIcon from "@material-ui/icons/MoveToInbox";
import MailIcon from "@material-ui/icons/Mail";
import Input from "@material-ui/core/Input";
import Axios from "axios";
import { useEffect } from "react";
import { useHistory } from "react-router-dom";
import Rating from "@material-ui/lab/Rating";
import { useState } from "react";

const useStyles = makeStyles({
  list: {
    width: 250,
  },
  fullList: {
    width: "auto",
  },
});

function MediaCard(props) {
  const classes = useStyles();
  const [name, setname] = React.useState('kl');

  return (
    <>
      {
        <Link href={'http://localhost:3001/files/' + props.filename}>
          <Card className={classes.root}>
            <CardActionArea>
              <CardMedia
                className={classes.media}
                image={MIT}
                title={props.filename}
              />
              <CardContent>
                <Typography gutterBottom variant='h5' component='h2'>
                
                  <h4>Book Name</h4>
                  {props.filename}
                  <p></p>
                </Typography>
                <Typography
                  variant='body2'
                  color='textSecondary'
                  component='p'
                ></Typography>
              </CardContent>
            </CardActionArea>
            <CardActions></CardActions>
          </Card>
        </Link>
      }
    </>
  );
}
export  function SearchProduct(props) {
 
  const [Files, setFiles] = React.useState([]);
  const [search,setsearch]=React.useState("");
  const address = 'http://localhost:3001/allfiles';
  React.useEffect(() => {
    Axios.post(address).then((result) => {
      console.log(result);
      setFiles(result.data.files);
    });
  }, []);
  

  return (
    <>
    <div className="App">
      <input type="text" placeholder="Search" onChange={event=>{setsearch(event.target.value)}}></input>{
        Files.filter((value)=>{
          if(search==="")
          {
            return value;
          }
          else if(value.filename.toLowerCase().includes(setFiles.toLowerCase))
          {
               return value;
          }
        }).map((value, key) => {
          return (
            <div>
              <p>{value.filename}</p>
            </div>
          )
        })
      }
    </div>
      {/*  */}
    </>
  );
}
