import React, { useCallback } from 'react';
import Axios from 'axios';
import Card from '@material-ui/core/Card';
import Link from '@material-ui/core/Link';
import "./Amit.css";
import { useHistory } from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import { toast } from 'react-toastify';
import CardActionArea from '@material-ui/core/CardActionArea';
import { GridList } from '@material-ui/core';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import MIT from '../../images/download.jfif';
import { Grid } from '@material-ui/core';

import { Container, Row, Col } from 'react-grid-system';
toast.configure();

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1.9,
    padding: theme.spacing(2),
    maxWidth: 700,
    marginBottom:0,
    marginTop:0.2,
    textAlign:"center",
    boxShadow:"5px 10px #888888",
    transition:"0.3s",
    borderRadius:"22px",
    perspective: "1000px",
    opacity: "0.9",
    cursor: "pointer",
    fontfamily: "Arial, Helvetica, sans-serif"
    
  },
  media: {
    height: 240,
    width:305,
    textAlign:"center",
    justifyContent:"center",
    fontfamily: "Arial, Helvetica, sans-serif"
  },
  
}));

function MediaCard(props) {
  const classes = useStyles();
  const [name, setname] = React.useState('kl');
  
  return (
    <>
      {
        <Link href={'http://localhost:3001/files/' + props.filename}>
          <Card className={classes.root}>
             <div className="column">
            <CardActionArea>
              <CardMedia
                className={classes.media}
                image={MIT}
                title={props.filename}
                fontfamily= "Arial, Helvetica, sans-serif"
                
              />
              <CardContent>
                <Typography gutterBottom variant='h5' component='h2'>
                
                  {/* <h6>File-</h6> */}
                  {props.filename}
                  <p></p>
                </Typography>
                <Typography
                  variant='body2'
                  fontfamily="Arial, Helvetica, sans-serif"
                  color='textSecondary'
                  component='p'
                ></Typography>
              </CardContent>
            </CardActionArea>
            <CardActions></CardActions>
            </div>
          </Card>
        </Link>
      }
    </>
  );
}

export default function HomePage() {
  const [Files, setFiles] = React.useState([]);
  const [search,setsearch]=React.useState("");
  const address = 'http://localhost:3001/allfiles';
  React.useEffect(() => {
    Axios.post(address).then((result) => {
      console.log(result);
      setFiles(result.data.files);
    });
  }, []);

  return (

    <div style={{background:'#12c2e9'}} >
       
        <div className="home" >
      <Container className="root-container">
        <Grid className="sample-grid" container spacing={1}>
        <form><input type="text" class="textbox" placeholder="Search" onChange={event=>{setsearch(event.target.value)}}></input></form>
          {Files.map((item,index) => {
            return (
              <>
             
             <Grid className="feedback-requesters-grid" container item xs={4}>
             <div style={{ marginRight: '1px', width: '25rem',marginTop:'30px',justifyContent:"center",textAlign:"center"}}>
              <MediaCard {...item} />
              </div>
              </Grid>
                
               
               
                
              </>
            );
          })}
        

       
      </Grid>
      </Container>
    </div>
    </div>
  );
  
}
