import React,{useState} from 'react';
import ReactDOM from "react-dom";
import Axios from 'axios';
export default function upload() 
{
    const [names,setNames]=useState({
        name:""
        
     });
     const InputEvent=(event)=>{
        const{value,name}=event.target;
        setNames((preValue)=>{
          if(name==="myfile")
          {
           return{
             name:value,
           };
          }
          
        });
      
};

    const onSubmit=(event)=>{
        event.preventDefault();
        
        Axios.post(
          `http://localhost:3001/upload`,
          {
            name:names.name
          }
          
        )
          .then((result) => {})
      alert("Thank You for your response");
      window.location.reload();
  }
    return (
        
<>
{/* <form onSubmit={onSubmit}></form> */}
  <h1>Show File-select Fields</h1>
  <h3>Show a file-select field which allows only one file to be chosen:</h3>
  <form action="/upload" method="POST" onSubmit={onSubmit} >
    <label htmlFor="myfile">Select a file:</label>
    <input type="file" id="myfile" name="myfile" onChange={InputEvent} value={names.myfile} />
    <br />
    <br />
    <input type="submit" />
  </form>
  
</> 
    );
}
