import React from "react";
import { Media } from "react-bootstrap";
import Button from "@material-ui/core/Button";
import "./subcategory.scss";
import clsx from "clsx";
import { makeStyles } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
import List from "@material-ui/core/List";
import Divider from "@material-ui/core/Divider";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import InboxIcon from "@material-ui/icons/MoveToInbox";
import Rating from "@material-ui/lab/Rating";
import MailIcon from "@material-ui/icons/Mail";
import Input from "@material-ui/core/Input";
import Axios from "axios";
import { useEffect } from "react";

const useStyles = makeStyles({
  list: {
    width: 250,
  },
  fullList: {
    width: "auto",
  },
});

function MediaCard(props) {
  const [rating, setRating] = React.useState(1.1);
  const [id, setid] = React.useState(props._id);
  const [miniPrice, setminiPrice] = React.useState(100000000);
  const [discription, setdiscription] = React.useState("Product");
  const [image, setimage] = React.useState("HII");
  for (var key in props.Sellers) {
    var obj = props.Sellers[key];
    if (parseInt(miniPrice, 10) > parseInt(obj.SellerPrice, 10)) {
      setminiPrice(obj.SellerPrice);
      setdiscription(obj.Description);
      setimage(obj.Image);
      setRating(obj.Rating.$numberDecimal);
    }
  }

  return (
    <>
      {parseInt(props.mini, 10) <= parseInt(miniPrice, 10) &&
        parseInt(props.maxi, 10) >= parseInt(miniPrice, 10) && (
          <div class="box-wrapper" style={{ margin: "10px" }}>
            <a href={"/product/" + id}>
              <img
                src={image}
                alt="rhcp"
                style={{ cursor: "pointer" }}
                width="450px"
                height="200px"
              />
            </a>
            <div class="box-content">
              <a class="buy">
                <span>
                  <img
                    style={{
                      marginTop: "10px",
                      cursor: "pointer",
                      height: "50px",
                      width: "50px",
                    }}
                    src="https://upload.wikimedia.org/wikipedia/commons/thumb/f/f1/Heart_coraz%C3%B3n.svg/1200px-Heart_coraz%C3%B3n.svg.png"
                  />
                </span>
              </a>
              <div class="title">{props.Name}</div>
              <div class="desc">
                <b>{props.Category}</b> :{" "}
                {discription.length > 20
                  ? discription.substring(0, 17) + "..."
                  : discription}
              </div>
              <span class="price">â‚¹ {miniPrice}</span>
              <div class="ssfooter stars1">
                <ul class="stars1">

                  <Rating
                    name="read-only"
                    value={rating}
                    precision={0.1}
                    readOnly
                  />
                  {"(" + rating + ")"}

                </ul>
              </div>
            </div>
            <div class="success"></div>
            <Button
              variant="contained"
              color="primary"
              className="addtocart"
              disableElevation
              style={{ height: "40px", width: "90%", marginBottom: "8px" }}
            >
              <p style={{ fontSize: "1.2rem" }}>ðŸ›’ Add to Cart ðŸ›’</p>
            </Button>
          </div>
        )}
    </>
  );
}

export function SubCategories(props) {
  const category = props.match.params.id;

  const [Products, setProducts] = React.useState([]);
  const address = "http://localhost:3001/api/categoryproducts/" + category;

  useEffect(() => {
    Axios.get(address).then((result) => {
      console.log(result);
      setProducts(result.data.products);
    });
  }, []);

  const classes = useStyles();
  const [state, setState] = React.useState({
    top: false,
    left: false,
    bottom: false,
    right: false,
  });

  const toggleDrawer = (anchor, open) => (event) => {
    if (
      event.type === "keydown" &&
      (event.key === "Tab" || event.key === "Shift")
    ) {
      return;
    }

    setState({ ...state, [anchor]: open });
  };

  const [minPrice, setminPrice] = React.useState(0);
  const [maxPrice, setmaxPrice] = React.useState(1000000000);
  const [miinPrice, setmiinPrice] = React.useState(0);
  const [maaxPrice, setmaaxPrice] = React.useState(1000000000);

  function settingmaximini() {
    setmaxPrice(maaxPrice);
    setminPrice(miinPrice);
  }

  const list = (anchor) => (
    <div
      className={clsx(classes.list, {
        [classes.fullList]: anchor === "top" || anchor === "bottom",
      })}
      role="presentation"
      onClick={toggleDrawer(anchor, true)}
      onKeyDown={toggleDrawer(anchor, true)}
    >
      <List>
        {[
          "Relevence",
          "Popularity",
          "Price -- Low to High",
          "Price -- High to Low",
        ].map((text, index) => (
          <ListItem button key={text}>
            <ListItemIcon>
              <InboxIcon />
            </ListItemIcon>
            <ListItemText primary={text} />
          </ListItem>
        ))}
      </List>
      <Divider />
      <List>
        {["Price Range"].map((text, index) => (
          <>
            <ListItem key={text}>
              <ListItemText primary={text + " : "} />
            </ListItem>
            <ListItem>
              <Input
                type="Number"
                placeholder="Min Price"
                onChange={(e) => setmiinPrice(e.target.value)}
              />
              <Input
                type="Number"
                placeholder="Max Price"
                onChange={(e) => setmaaxPrice(e.target.value)}
              />
            </ListItem>
            <ListItem>
              {parseInt(miinPrice, 10) > 0 &&
                parseInt(maaxPrice, 10) < 100000 &&
                parseInt(miinPrice, 10) <= parseInt(maaxPrice, 10) && (
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={settingmaximini}
                  >
                    Check
                  </Button>
                )}
            </ListItem>
          </>
        ))}
      </List>
    </div>
  );

  if (
    category === "Stationery" ||
    category === "Grocery" ||
    category === "Electronics" ||
    category === "Footwear" ||
    category === "Sports" ||
    category === "Books"
  )
    return (
      <>
        <div
          style={{
            height: "60px",
            textAlign: "center",
            marginBottom: "20px",
            color: "#000",
            zIndex: "100",
          }}
        >
          <h1 style={{ margin: "10px" }}>
            <b>Explore {category} : </b>
            {["left"].map((anchor) => (
              <React.Fragment key={anchor}>
                <Button
                  variant="contained"
                  color="primary"
                  onClick={toggleDrawer(anchor, true)}
                >
                  Filter
                </Button>
                <Drawer
                  anchor={anchor}
                  open={state[anchor]}
                  onClose={toggleDrawer(anchor, false)}
                >
                  {list(anchor)}
                </Drawer>
              </React.Fragment>
            ))}
            <br />
          </h1>
        </div>
        <div style={{ margin: "auto", width: "90%" }}>
          {Products.map((item) => {
            item.mini = minPrice;
            item.maxi = maxPrice;
            return (
              <>
                <MediaCard {...item} />
              </>
            );
          })}
        </div>
      </>
    );
  else {
    return (
      <>
        <h1>Page Not Found</h1>
      </>
    );
  }
}
