import React, { useCallback } from 'react';
import Axios from 'axios';
import Card from '@material-ui/core/Card';
import Link from '@material-ui/core/Link';
import { useHistory } from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import { toast } from 'react-toastify';
import CardActionArea from '@material-ui/core/CardActionArea';
import { GridList } from '@material-ui/core';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import MIT from '../../images/LocoCart.PNG';
import { Container, Row, Col } from 'react-grid-system';
toast.configure();

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1.6,
    padding: theme.spacing(6),
    maxWidth: 380,
  },
  media: {
    height: 140,
  },
  
}));

function MediaCard(props) {
  const classes = useStyles();
  const [name, setname] = React.useState('kl');

  return (
    <>
      {
        <Link href={'http://localhost:3001/files/' + props.filename}>
          <Card className={classes.root}>
            <CardActionArea>
              <CardMedia
                className={classes.media}
                image={MIT}
                title={props.filename}
                backgroundColor='yellow'
              />
              <CardContent>
                <Typography gutterBottom variant='h5' component='h2'>
                
                  <h4>Book Name</h4>
                  {props.filename}
                  <p></p>
                </Typography>
                <Typography
                  variant='body2'
                  color='textSecondary'
                  component='p'
                ></Typography>
              </CardContent>
            </CardActionArea>
            <CardActions></CardActions>
          </Card>
        </Link>
      }
    </>
  );
}

export default function HomePage() {
  const [Files, setFiles] = React.useState([]);
  const address = 'http://localhost:3001/allfiles';
  React.useEffect(() => {
    Axios.post(address).then((result) => {
      console.log(result);
      setFiles(result.data.files);
    });
  }, []);

  return (
    <div style={{ marginRight: '20px', width: '22rem',marginTop:'5px',display: "flex", flexWrap: "wrap" }}>
      {
        <div style={{ marginRight: '20px', width: '22rem',marginTop:'5px',display: 'flex', justifyContent: 'space-between', alignItems: 'center',marginBottom: "10px"}}>
          {/* <GridList cols={3}> */}
          {/* <Row xs={3}> */}
          <GridList cellHeight={180} cols={3} >
          {Files.map((item) => {
            return (
              <>
              <div style={{ margin: "auto", width: "90%",marginTop:'5px',float: "right",display: "table"}}>
              
                <MediaCard {...item} />
                </div>
                
              </>
            );
          })}
          
          </GridList>
        </div>
      }
    </div>
  );
}
