import React from "react";
import { Media } from "react-bootstrap";
// import "../Category/SubCategory/subcategory.scss";
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import Card from '@material-ui/core/Card';
import Link from '@material-ui/core/Link';
import clsx from "clsx";
import { makeStyles } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
import Button from "@material-ui/core/Button";
import List from "@material-ui/core/List";
import Divider from "@material-ui/core/Divider";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import InboxIcon from "@material-ui/icons/MoveToInbox";
import MailIcon from "@material-ui/icons/Mail";
import Input from "@material-ui/core/Input";
import Axios from "axios";
import { useEffect } from "react";
import { useHistory } from "react-router-dom";
import Rating from "@material-ui/lab/Rating";
import MIT from '../../images/LocoCart.PNG';

const useStyles = makeStyles({
  list: {
    width: 250,
  },
  fullList: {
    width: "auto",
  },
});

function MediaCard(props) {
  const classes = useStyles();
  const [name, setname] = React.useState('kl');
  console.log(props.name);
  setname(props.name);

  return (
    <>
      {
        <Link href={'http://localhost:3001/files/' + props.filename}>
          <Card className={classes.root}>
            <CardActionArea>
              <CardMedia
                className={classes.media}
                image={MIT}
                title={props.filename}
              />
              <CardContent>
                <Typography gutterBottom variant='h5' component='h2'>
                
                  <h4>Book Name</h4>
                  {props.filename}
                  <p></p>
                </Typography>
                <Typography
                  variant='body2'
                  color='textSecondary'
                  component='p'
                ></Typography>
              </CardContent>
            </CardActionArea>
            <CardActions></CardActions>
          </Card>
        </Link>
      }
    </>
  );
}

export  function SearchProduct(props) {
  // window.onload = function () {
  //   if (!window.location.hash) {
  //     window.location = window.location + "#?=Searched";
  //     window.location.reload();
  //   }
  // };
  const search = props.match.params.id;
  const [lastsearched, setlastsearched] = React.useState(props.match.params.id);
  localStorage.setItem("LastSearched", props.match.params.id);

  const [Files, setProducts] = React.useState([]);
  const address = "http://localhost:3001/search";

  useEffect(() => {
    Axios.post(address, { name: search }).then((result) => {
      console.log(result);
      setProducts(result.data.products);
    });
  }, []);

  const classes = useStyles();
  const [state, setState] = React.useState({
    top: false,
    left: false,
    bottom: false,
    right: false,
  });

  const toggleDrawer = (anchor, open) => (event) => {
    if (
      event.type === "keydown" &&
      (event.key === "Tab" || event.key === "Shift")
    ) {
      return;
    }

    setState({ ...state, [anchor]: open });
  };

  const [minPrice, setminPrice] = React.useState(0);
  const [maxPrice, setmaxPrice] = React.useState(1000000000);
  const [miinPrice, setmiinPrice] = React.useState(0);
  const [maaxPrice, setmaaxPrice] = React.useState(1000000000);
  const [name,setname]=React.useState("a");

  function settingmaximini() {
    setmaxPrice(maaxPrice);
    setminPrice(miinPrice);
    setname(name);
  }

  const list = (anchor) => (
    <div
      className={clsx(classes.list, {
        [classes.fullList]: anchor === "top" || anchor === "bottom",
      })}
      role="presentation"
      onClick={toggleDrawer(anchor, true)}
      onKeyDown={toggleDrawer(anchor, true)}
    >
      <List>
        {[
          "Relevence",
          "Popularity",
          "Price -- Low to High",
          "Price -- High to Low",
        ].map((text, index) => (
          <ListItem button key={text}>
            <ListItemIcon>
              <InboxIcon />
            </ListItemIcon>
            <ListItemText primary={text} />
          </ListItem>
        ))}
      </List>
      <Divider />
      <List>
        {["Price Range"].map((text, index) => (
          <>
            <ListItem key={text}>
              <ListItemText primary={text + " : "} />
            </ListItem>
            <ListItem>
              <Input
                type="Number"
                placeholder="Min Price"
                onChange={(e) => setmiinPrice(e.target.value)}
              />

                <Input
                type="text"
                placeholder="Name"
                onChange={(e) => setname(e.target.value)}
              />
              
              <Input
                type="Number"
                placeholder="Max Price"
                onChange={(e) => setmaaxPrice(e.target.value)}
              />
            </ListItem>
            <ListItem>
              {parseInt(miinPrice, 10) > 0 &&
                parseInt(maaxPrice, 10) < 100000 &&
                parseInt(miinPrice, 10) <= parseInt(maaxPrice, 10) && (
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={settingmaximini}
                  >
                    Check
                  </Button>
                )}
            </ListItem>
          </>
        ))}
      </List>
    </div>
  );

  return (
    <>
     
      <div
        style={{
          height: "60px",
          textAlign: "center",
          marginBottom: "20px",
          color: "#000",
          zIndex: "100",
        }}
      >
        <h1 style={{ margin: "10px" }}>
          <b>Search Result For "{search}" : </b>

          {["left"].map((anchor) => (
            <React.Fragment key={anchor}>
              <Button
                variant="contained"
                color="primary"
                onClick={toggleDrawer(anchor, true)}
              >
                Filter
              </Button>
              <Drawer
                anchor={anchor}
                open={state[anchor]}
                onClose={toggleDrawer(anchor, false)}
              >
                {list(anchor)}
              </Drawer>
            </React.Fragment>
          ))}

          <br />
        </h1>
      </div>
      <div style={{ margin: "auto", width: "90%" }}>
        {Files.map((item) => {
          item.mini = minPrice;
          item.maxi = maxPrice;
          item.name=name;
          return (
            <>
              <MediaCard {...item} />
            </>
          );
        })}
      </div>
    </>
  );
}
