  //     </List>
  //   </div>
  // );

  return (
    <>
      {lastsearched != localStorage.getItem("LastSearched") &&
        window.location.reload(false)}
      <div
        style={{
          height: "60px",
          textAlign: "center",
          marginBottom: "20px",
          color: "#000",
          zIndex: "100",
